$.validator.addMethod("filetype",function(n,t,i){if(t.files.length===0)return!0;var r=n.split(".").pop().toLowerCase();return i.split(",").indexOf(r)!==-1},"Please select a valid image file (png, jpg).");$.validator.addMethod("filesize",function(n,t,i){return t.files[0].size<=i},"File must be less than 5MB.");$.validator.addMethod("filerequired",function(n,t){return t.files[0].size>0},"This file is empty.");